package CoreJava;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  //////////////////FOR LOOP*****************************
		  //For Incrementation
		//BREAK -- BREAKS THE FOR LOOP
		  int i;
		  
		   for(i=0; i<5; i++) {
		    System.out.println("Value of I is " + i);
		   }
		   int j;
		   //for decrementation
		   for(j=5; j > 0 ; j--) {
		    System.out.println("valueo of j is " + j);
		   }
	}

}
