package CoreJava;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int a = 1;
		  int b = 10;
		  
		  if (a == b) {
		   System.out.println("Value of a and b is same");
		  }
		  else if (a<b) {
		   System.out.println("a is smaller than b");
		  }
		  
		  else if (b > a) {
		   System.out.println("b is greater than a");
		  }
	}

}
